import React, { useState, useEffect } from 'react';
// Виправлений шлях до contentService
import { contentService } from '../../../services/contentService';
import './GalleryPage.css';

const GalleryPage = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadGalleryImages();
  }, []);

  const loadGalleryImages = async () => {
    try {
      // Тут ми отримуємо список зображень з бази даних
      const response = await contentService.getGalleryImages();
      setImages(response.data.images || []);
    } catch (error) {
      console.error('Помилка завантаження зображень:', error);
      // Якщо API ще не реалізоване, використовуємо тестові дані
      setImages([
        { id: 1, src: '/img/gallery1.jpg', alt: 'Галерея 1', category: 'events' },
        { id: 2, src: '/img/gallery2.jpg', alt: 'Галерея 2', category: 'events' },
        { id: 3, src: '/img/gallery3.jpg', alt: 'Галерея 3', category: 'classes' },
        { id: 4, src: '/img/gallery4.jpg', alt: 'Галерея 4', category: 'classes' },
        { id: 5, src: '/img/gallery5.jpg', alt: 'Галерея 5', category: 'concerts' },
        { id: 6, src: '/img/gallery6.jpg', alt: 'Галерея 6', category: 'concerts' },
        { id: 7, src: '/img/gallery7.jpg', alt: 'Галерея 7', category: 'events' },
        { id: 8, src: '/img/gallery8.jpg', alt: 'Галерея 8', category: 'classes' },
      ]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="gallery-page">
        <div className="container">
          <div className="loading">Завантаження галереї...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="gallery-page">
      <div className="container">
        <h1>Фото/Відео Галерея</h1>
        <p>Наші найкращі моменти та досягнення</p>

        <div className="gallery-grid">
          {images.map((image) => (
            <div key={image.id} className="gallery-item">
              <img 
                src={image.src} 
                alt={image.alt}
                loading="lazy"
              />
              <div className="image-overlay">
                <span className="image-title">{image.alt}</span>
              </div>
            </div>
          ))}
        </div>

        {images.length === 0 && (
          <div className="empty-gallery">
            <p>Поки що немає доданих фотографій. Будь ласка, завітайте пізніше.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default GalleryPage;
