import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/auth/Login';
import Dashboard from './components/dashboard/Dashboard';
import Editor from './components/editor/Editor';
import { authService } from './services/authService';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Перевіряємо чи користувач авторизований при завантаженні
    const checkAuth = () => {
      const authenticated = authService.isAuthenticated();
      setIsAuthenticated(authenticated);
      setLoading(false);
    };

    checkAuth();
  }, []);

  if (loading) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh' 
      }}>
        <div>Завантаження...</div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route 
            path="/login" 
            element={
              !isAuthenticated ? <Login /> : <Navigate to="/admin/dashboard" />
            } 
          />
          <Route 
            path="/admin/*" 
            element={
              isAuthenticated ? <AdminLayout /> : <Navigate to="/login" />
            } 
          />
          <Route path="/" element={<Navigate to="/admin/dashboard" />} />
        </Routes>
      </div>
    </Router>
  );
}

// Компонент для захищених маршрутів адмін-панелі
const AdminLayout = () => {
  return (
    <div>
      {/* Тут можна додати загальний хедер для адмінки */}
      <Routes>
        <Route path="dashboard" element={<Dashboard />} />
        <Route path="editor/:pageName" element={<Editor />} />
        <Route path="" element={<Navigate to="dashboard" />} />
      </Routes>
    </div>
  );
};

export default App;
