import React from 'react';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const pages = [
    { name: '–ì–æ–ª–æ–≤–Ω–∞ —Å—Ç–æ—Ä—ñ–Ω–∫–∞', path: 'home', description: '–†–µ–¥–∞–≥—É–≤–∞—Ç–∏ –≥–æ–ª–æ–≤–Ω—É —Å—Ç–æ—Ä—ñ–Ω–∫—É —Å–∞–π—Ç—É' },
    { name: '–ü—Ä–æ –ª—ñ—Ü–µ–π', path: 'about', description: '–Ü–Ω—Ñ–æ—Ä–º–∞—Ü—ñ—è –ø—Ä–æ –∑–∞–∫–ª–∞–¥' },
    { name: '–ö–æ–Ω—Ç–∞–∫—Ç–∏', path: 'contact', description: '–ö–æ–Ω—Ç–∞–∫—Ç–Ω–∞ —ñ–Ω—Ñ–æ—Ä–º–∞—Ü—ñ—è' },
    { name: '–ù–æ–≤–∏–Ω–∏', path: 'news', description: '–£–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è –Ω–æ–≤–∏–Ω–∞–º–∏' },
    { name: '–î–æ–∫—É–º–µ–Ω—Ç–∏', path: 'documents', description: '–ù–æ—Ä–º–∞—Ç–∏–≤–Ω—ñ –¥–æ–∫—É–º–µ–Ω—Ç–∏' }
  ];

  return (
    <div style={{ padding: '2rem', maxWidth: '1200px', margin: '0 auto' }}>
      <h1 style={{ fontSize: '2.5rem', marginBottom: '2rem', color: '#2c3e50' }}>
        –ê–¥–º—ñ–Ω-–ø–∞–Ω–µ–ª—å –£–º–∞–Ω—Å—å–∫–æ–≥–æ –ª—ñ—Ü–µ—é ‚Ññ2
      </h1>

      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
        gap: '1.5rem',
        marginTop: '2rem'
      }}>
        {pages.map((page) => (
          <div key={page.path} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
            <Link
              to={`/admin/visual-editor/${page.path}`}
              style={{
                background: 'white',
                padding: '2rem',
                borderRadius: '10px',
                boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                textDecoration: 'none',
                color: 'inherit',
                border: '2px solid transparent',
                transition: 'all 0.3s ease',
                display: 'block'
              }}
              onMouseOver={(e) => {
                e.currentTarget.style.borderColor = '#3498db';
                e.currentTarget.style.transform = 'translateY(-5px)';
              }}
              onMouseOut={(e) => {
                e.currentTarget.style.borderColor = 'transparent';
                e.currentTarget.style.transform = 'translateY(0)';
              }}
            >
              <h3 style={{ 
                fontSize: '1.5rem', 
                marginBottom: '1rem',
                color: '#2c3e50'
              }}>
                {page.name}
              </h3>
              <p style={{ 
                color: '#7f8c8d',
                margin: 0
              }}>
                {page.description}
              </p>
              <div style={{
                marginTop: '1rem',
                padding: '0.5rem',
                background: '#ecf0f1',
                borderRadius: '5px',
                fontSize: '0.9rem',
                color: '#34495e'
              }}>
                üìù –í—ñ–∑—É–∞–ª—å–Ω–µ —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è
              </div>
            </Link>

            {/* –ö–Ω–æ–ø–∫–∞ –¥–ª—è —Å—Ç–∞—Ä–æ–≥–æ —Ä–µ–¥–∞–∫—Ç–æ—Ä–∞ */}
            <Link
              to={`/admin/editor/${page.path}`}
              style={{
                textAlign: 'center',
                padding: '0.5rem',
                background: '#95a5a6',
                color: 'white',
                textDecoration: 'none',
                borderRadius: '5px',
                fontSize: '0.8rem'
              }}
            >
              –°—Ç–∞—Ä–∏–π —Ä–µ–¥–∞–∫—Ç–æ—Ä (TinyMCE)
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
