import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { contentService } from '../../services/contentService';
import SiteLayout from './SiteLayout';
import './SitePage.css';

const SitePage = () => {
  const { pageName } = useParams();
  const [pageData, setPageData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadPageData();
  }, [pageName]);

  const loadPageData = async () => {
    try {
      const response = await contentService.getContent(pageName);
      if (response.data && response.data.content) {
        setPageData({
          title: getPageTitle(pageName),
          content: response.data.content
        });
      } else {
        setPageData(getDefaultTemplate(pageName));
      }
    } catch (error) {
      console.error('Помилка завантаження:', error);
      setPageData(getDefaultTemplate(pageName));
    } finally {
      setLoading(false);
    }
  };

  const getPageTitle = (name) => {
    const titles = {
      home: 'Уманська дитяча школа мистецтв «Антарес»',
      about: 'Про школу',
      departments: 'Відділи',
      gallery: 'Галерея',
      transparency: 'Прозорість',
      events: 'Події',
      contacts: 'Контакти'
    };
    return titles[name] || name;
  };

  const getDefaultTemplate = (pageName) => {
    const templates = {
      home: {
        title: 'Уманська дитяча школа мистецтв «Антарес»',
        content: `
          <h1>Уманська дитяча школа мистецтв «Антарес»</h1>

          <div class="promo-banner">
            <span class="promo-text">Ознайомчий безкоштовний урок</span>
            <a href="https://forms.gle/ВАШ_ЛІНК" target="_blank" class="promo-button">Записатись</a>
          </div>

          <!-- ABOUT SECTION -->
          <section class="about">
            <div class="about-content">
              <img src="/img/school-image.jpg" alt="Будівля школи">
              <div class="about-text">
                <h2>Про нашу школу</h2>
                <p>Фахівці з багаторічним досвідом роботи з дітьми, індивідуальний підхід до кожного учня. Ми створюємо доброзичливе середовище для творчості.</p>
                <a href="/site/about" class="learn-more">Дізнатись більше →</a>
                <button class="promo-button" onclick="document.getElementById('signupModal').style.display='block'">Записатись на урок</button>
              </div>
            </div>
          </section>

          <!-- ACTIVITIES -->
          <section class="activities">
            <h2>Відділи</h2>
            <p>Тут ви можете визначитись, що вас надихає</p>
            <div class="activity-list">
              <div class="activity">
                <img src="/img/music1.jpg" alt="Оркестрові інструменти">
                <h3>Оркестрові інструменти</h3>
                <a href="/site/departments">Дізнатись більше</a>
              </div>
              <div class="activity">
                <img src="/img/music2.jpg" alt="Хоровий спів">
                <h3>Хоровий спів</h3>
                <a href="/site/departments">Дізнатись більше</a>
              </div>
              <div class="activity">
                <img src="/img/music3.jpg" alt="Музичні інструменти">
                <h3>Музичні інструменти</h3>
                <a href="/site/departments">Дізнатись більше</a>
              </div>
            </div>
          </section>
        `
      },
      about: {
        title: 'Про школу',
        content: `
          <section class="about-content-section">
            <div class="container">
              <h1>Про нашу школу</h1>
              <div class="about-details">
                <img src="/img/about-details.jpg" alt="Детальніше про школу">
                <div class="about-text">
                  <p>Уманська дитяча школа мистецтв «Антарес» - це сучасний навчальний заклад, де діти розкривають свої творчі здібності.</p>
                  <p>Наші педагоги - це фахівці з багаторічним досвідом, які знаходять індивідуальний підхід до кожного учня.</p>
                </div>
              </div>
            </div>
          </section>
        `
      },
      departments: {
        title: 'Відділи',
        content: `
          <section class="departments-section">
            <div class="container">
              <h1>Наші відділи</h1>
              <div class="department-list">
                <div class="department">
                  <img src="/img/department-music.jpg" alt="Музичний відділ">
                  <h3>Музичний відділ</h3>
                  <p>Навчання грі на різних музичних інструментах</p>
                </div>
                <div class="department">
                  <img src="/img/department-art.jpg" alt="Художній відділ">
                  <h3>Художній відділ</h3>
                  <p>Розвиток художніх талантів та творчого мислення</p>
                </div>
              </div>
            </div>
          </section>
        `
      }
    };

    return templates[pageName] || {
      title: getPageTitle(pageName),
      content: `
        <section class="default-section">
          <div class="container">
            <h1>${getPageTitle(pageName)}</h1>
            <p>Ця сторінка ще не створена. Ви можете додати контент через адмін-панель.</p>
            <a href="/site/home">← На головну</a>
          </div>
        </section>
      `
    };
  };

  if (loading) {
    return (
      <SiteLayout>
        <div className="site-loading">
          <div>Завантаження сторінки...</div>
        </div>
      </SiteLayout>
    );
  }

  return (
    <SiteLayout>
      <div dangerouslySetInnerHTML={{ __html: pageData.content }} />
    </SiteLayout>
  );
};

export default SitePage;
