import React from 'react';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const pages = [
    { name: 'Головна', path: 'home' },
    { name: 'Про школу', path: 'about' },
    { name: 'Контакти', path: 'contact' },
    { name: 'Новини', path: 'news' },
    { name: 'Розклад', path: 'schedule' },
    { name: 'Вчителі', path: 'teachers' }
  ];

  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-6">Адмін панель школи</h1>

      <div className="grid" style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', 
        gap: '1rem' 
      }}>
        {pages.map((page) => (
          <Link
            key={page.path}
            to={`/admin/editor/${page.path}`}
            className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-all border block no-underline text-inherit"
          >
            <h3 className="text-xl font-semibold mb-2">{page.name}</h3>
            <p className="text-gray-600">Редагувати контент сторінки</p>
          </Link>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
