import api from './api';

const galleryService = {
  // Отримати всі фото галереї
  getGalleryImages: async () => {
    try {
      const response = await api.get('/gallery');
      return response.data;
    } catch (error) {
      console.error('Помилка отримання фото:', error);
      throw error;
    }
  },

  // Завантажити нове фото
  uploadGalleryImage: async (formData) => {
    try {
      // Спочатку завантажуємо файл
      const uploadResponse = await api.post('/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      // Потім створюємо запис в галереї
      const imageData = {
        title: formData.get('title'),
        description: formData.get('description'),
        image_url: uploadResponse.data.url
      };

      const galleryResponse = await api.post('/gallery', imageData);
      return galleryResponse.data;
    } catch (error) {
      console.error('Помилка завантаження фото:', error);
      throw error;
    }
  },

  // Видалити фото
  deleteGalleryImage: async (imageId) => {
    try {
      const response = await api.delete(`/gallery/${imageId}`);
      return response.data;
    } catch (error) {
      console.error('Помилка видалення фото:', error);
      throw error;
    }
  },

  // Оновити інформацію про фото
  updateGalleryImage: async (imageId, data) => {
    try {
      const response = await api.put(`/gallery/${imageId}`, data);
      return response.data;
    } catch (error) {
      console.error('Помилка оновлення фото:', error);
      throw error;
    }
  }
};

export default galleryService;
