import api from './api';

export const contentService = {
  // Отримати контент сторінки
  getContent: (pageName) => 
    api.get(`/content/${pageName}`),

  // Оновити контент сторінки
  updateContent: (pageName, content) => 
    api.put(`/content/${pageName}`, { content }),

  // Отримати список всіх сторінок
  getPages: () => 
    api.get('/content/pages')
};
