import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { contentService } from '../../services/contentService';
import './SitePage.css';

const SitePage = () => {
  const { pageName } = useParams();
  const [pageData, setPageData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadPageData();
  }, [pageName]);

  const loadPageData = async () => {
    try {
      const response = await contentService.getContent(pageName);
      if (response.data) {
        // Якщо дані є в базі
        if (typeof response.data.content === 'string') {
          try {
            // Спробуємо розпарсити JSON
            const parsedData = JSON.parse(response.data.content);
            setPageData(parsedData);
          } catch {
            // Якщо не JSON, використовуємо як простий текст
            setPageData({
              title: pageName,
              sections: [{ id: 1, type: 'content', content: response.data.content }]
            });
          }
        } else {
          setPageData(response.data);
        }
      } else {
        // Якщо даних немає, використовуємо шаблон
        setPageData(getDefaultTemplate());
      }
    } catch (error) {
      console.error('Помилка завантаження:', error);
      setPageData(getDefaultTemplate());
    } finally {
      setLoading(false);
    }
  };

  const getDefaultTemplate = () => ({
    title: "УМАНСЬКИЙ ЛІЦЕЙ № 2",
    header: {
      logo: "/logo.png",
      menu: ["Головна", "Мій ліцей", "Нормативно-правова база", "Діяльність ліцею", "Форми навчання", "Досягнення педагогічного колективу"]
    },
    sections: [
      {
        id: 'hero',
        type: 'hero',
        content: `
          <h1>Уманський ліцей № 2</h1>
          <h2>Уманської міської ради Черкаської області</h2>
          <p>ЗАПРОШУЄ НА НАВЧАННЯ ЗДОБУВАЧІВ ОСВІТИ ДО 5-11-Х КЛАСІВ.</p>
          <a href="https://docs.google.com/forms/d/1qGynAqMyNhabwivzZtY9kXX9_ChNE49KsfcKbKfzMwo/viewform" class="cta-button">Подати заявку</a>
        `
      },
      {
        id: 'content',
        type: 'content',
        content: `
          <h3>Прозорість та інформаційна відкритість діяльності закладу</h3>
          <p>Тут буде інформація про прозорість діяльності...</p>

          <h3>Виховна робота</h3>
          <p>Інформація про виховну роботу...</p>

          <h3>Оголошення</h3>
          <p>Актуальні оголошення...</p>

          <h3>Відсотока</h3>
          <p>Інформація про відсотоку...</p>
        `
      }
    ]
  });

  const getPageTitle = (name) => {
    const titles = {
      home: 'Головна сторінка',
      about: 'Про школу',
      contact: 'Контакти',
      news: 'Новини',
      teachers: 'Вчителі',
      schedule: 'Розклад'
    };
    return titles[name] || name;
  };

  if (loading) {
    return (
      <div className="site-loading">
        <div>Завантаження сторінки...</div>
      </div>
    );
  }

  if (!pageData) {
    return (
      <div className="site-error">
        <h1>Сторінку не знайдено</h1>
        <p>Сторінка "{pageName}" не існує.</p>
        <Link to="/site/home">Перейти на головну</Link>
      </div>
    );
  }

  return (
    <div className="site-page">
      {/* Хедер сайту */}
      <header className="site-header">
        <div className="header-content">
          <div className="site-title">
            <h1>{pageData.title || getPageTitle(pageName)}</h1>
          </div>

          <nav className="main-menu">
            <ul>
              <li><Link to="/site/home">Головна</Link></li>
              <li><Link to="/site/about">Про ліцей</Link></li>
              <li><Link to="/site/contact">Контакти</Link></li>
              <li><Link to="/site/news">Новини</Link></li>
              <li><Link to="/admin/dashboard" style={{ color: '#e74c3c' }}>Адмін-панель</Link></li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Основний контент */}
      <main className="site-content">
        {pageData.sections && pageData.sections.map(section => (
          <section key={section.id} className={`content-section ${section.type}`}>
            <div 
              className="section-content"
              dangerouslySetInnerHTML={{ __html: section.content }}
            />
          </section>
        ))}

        {/* Якщо секцій немає, показуємо простий контент */}
        {(!pageData.sections || pageData.sections.length === 0) && pageData.content && (
          <section className="content-section">
            <div 
              className="section-content"
              dangerouslySetInnerHTML={{ __html: pageData.content }}
            />
          </section>
        )}
      </main>

      {/* Футер */}
      <footer className="site-footer">
        <div className="footer-content">
          <p>&copy; 2024 Уманський ліцей № 2. Всі права захищені.</p>
          <p>
            <a href="/admin/dashboard" style={{ color: '#3498db', fontSize: '0.9rem' }}>
              Вхід для адміністратора
            </a>
          </p>
        </div>
      </footer>
    </div>
  );
};

export default SitePage;
