import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/auth/Login';
import Dashboard from './components/dashboard/Dashboard';
import Editor from './components/editor/Editor';
import VisualEditor from './components/visual-editor/VisualEditor';
import SitePage, { NewsPage } from './components/site/SitePage';
import NewsManagement from './components/admin/NewsManagement';
import NewsEditor from './components/admin/NewsEditor';
import { authService } from './services/authService';
import AdminGallery from './components/admin/AdminGallery';
import GalleryManagement from './components/admin/GalleryManagement';

import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(null);

  useEffect(() => {
    const checkAuth = () => {
      const authenticated = authService.isAuthenticated();
      setIsAuthenticated(authenticated);
    };

    checkAuth();
  }, []);

  if (isAuthenticated === null) {
    return <div>–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...</div>;
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* –ü—É–±–ª—ñ—á–Ω—ñ –º–∞—Ä—à—Ä—É—Ç–∏ */}
          <Route path="/site/:pageName" element={<SitePage />} />
          <Route path="/site" element={<Navigate to="/site/home" replace />} />

          {/* –û–∫—Ä–µ–º–∏–π –º–∞—Ä—à—Ä—É—Ç –¥–ª—è –Ω–æ–≤–∏–Ω –∑ ID */}
          <Route path="/site/news/:id" element={<NewsPage />} />

          {/* –ê–¥–º—ñ–Ω-–º–∞—Ä—à—Ä—É—Ç–∏ */}
          <Route 
            path="/login" 
            element={!isAuthenticated ? <Login /> : <Navigate to="/admin/dashboard" replace />} 
          />
          <Route 
            path="/admin/*" 
            element={isAuthenticated ? <AdminLayout /> : <Navigate to="/login" replace />} 
          />

          <Route path="/" element={<Navigate to="/site/home" replace />} />
          <Route path="*" element={<NotFound />} />
        </Routes>
      </div>
    </Router>
  );
}

const AdminLayout = () => {
  const handleLogout = () => {
    authService.logout();
    window.location.href = '/login';
  };

  return (
    <div>
      <header style={{
        backgroundColor: '#1f2937',
        color: 'white',
        padding: '1rem 2rem',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <h1 style={{ margin: 0, fontSize: '1.5rem' }}>–ê–¥–º—ñ–Ω-–ø–∞–Ω–µ–ª—å —à–∫–æ–ª–∏</h1>
        <div>
          <a 
            href="/site/home" 
            style={{
              color: 'white',
              marginRight: '1rem',
              textDecoration: 'underline'
            }}
            target="_blank"
            rel="noopener noreferrer"
          >
            üëÅÔ∏è –ü–µ—Ä–µ–≥–ª—è–¥ —Å–∞–π—Ç—É
          </a>
          <button
            onClick={handleLogout}
            style={{
              backgroundColor: '#ef4444',
              color: 'white',
              border: 'none',
              padding: '0.5rem 1rem',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            –í–∏–π—Ç–∏
          </button>
        </div>
      </header>

      <Routes>
        <Route path="dashboard" element={<Dashboard />} />
        <Route path="editor/:pageName" element={<Editor />} />
        <Route path="visual-editor/:pageName" element={<VisualEditor />} />

        {/* –ú–∞—Ä—à—Ä—É—Ç–∏ –¥–ª—è —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è –Ω–æ–≤–∏–Ω–∞–º–∏ */}
        <Route path="news" element={<NewsManagement />} />
        <Route path="news/create" element={<NewsEditor />} />
        <Route path="news/edit/:id" element={<NewsEditor />} />

        <Route path="" element={<Navigate to="dashboard" replace />} />
        <Route path="/admin/gallery-management" element={<AdminGallery />} />
        <Route path="/admin/gallery-management" element={<GalleryManagement />} />
      </Routes>
    </div>
  );
};

const NotFound = () => {
  return (
    <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center', 
      height: '100vh',
      flexDirection: 'column',
      textAlign: 'center'
    }}>
      <h1 style={{ fontSize: '3rem', color: '#2c3e50', marginBottom: '1rem' }}>404</h1>
      <h2 style={{ color: '#7f8c8d', marginBottom: '2rem' }}>–°—Ç–æ—Ä—ñ–Ω–∫—É –Ω–µ –∑–Ω–∞–π–¥–µ–Ω–æ</h2>
      <div style={{ display: 'flex', gap: '1rem' }}>
        <a 
          href="/site/home"
          style={{
            backgroundColor: '#4AAFF7',
            color: 'white',
            padding: '0.75rem 1.5rem',
            textDecoration: 'none',
            borderRadius: '5px',
            fontWeight: 'bold'
          }}
        >
          –ù–∞ –≥–æ–ª–æ–≤–Ω—É
        </a>
        <a 
          href="/admin/dashboard"
          style={{
            backgroundColor: '#9b59b6',
            color: 'white',
            padding: '0.75rem 1.5rem',
            textDecoration: 'none',
            borderRadius: '5px',
            fontWeight: 'bold'
          }}
        >
          –í –∞–¥–º—ñ–Ω–∫—É
        </a>
      </div>
    </div>
  );
};

export default App;
