import React, { useState, useEffect } from 'react';
import { transparencyService } from '../../services/transparencyService';
import RichTextEditor from '../editor/RichTextEditor';
import './TransparencyManagement.css';

const TransparencyManagement = () => {
  const [sections, setSections] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingSection, setEditingSection] = useState(null);
  const [saveStatus, setSaveStatus] = useState('');
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    loadSections();
  }, []);

  const loadSections = async () => {
    try {
      const data = await transparencyService.getAll();
      setSections(data);
      setLoading(false);
    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ä–æ–∑–¥—ñ–ª—ñ–≤:', error);
      setLoading(false);
    }
  };

  const handleEdit = (section) => {
    setEditingSection({ 
      ...section,
      newDocuments: []
    });
  };

  const handleContentChange = (content) => {
    setEditingSection(prev => ({
      ...prev,
      content: content
    }));
  };

  const handleFileUpload = async (event) => {
    const files = Array.from(event.target.files);
    if (!files.length) return;

    setUploading(true);

    try {
      const uploadedDocuments = [];

      for (const file of files) {
        const formData = new FormData();
        formData.append('file', file);

        const response = await fetch('/api/upload', {
          method: 'POST',
          body: formData,
        });

        if (response.ok) {
          const result = await response.json();
          uploadedDocuments.push({
            name: file.name,
            url: result.filePath,
            type: file.type,
            size: file.size
          });
        }
      }

      setEditingSection(prev => ({
        ...prev,
        documents: [...(prev.documents || []), ...uploadedDocuments]
      }));

      setSaveStatus(`‚úÖ –£—Å–ø—ñ—à–Ω–æ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–æ ${uploadedDocuments.length} —Ñ–∞–π–ª—ñ–≤`);
      setTimeout(() => setSaveStatus(''), 3000);

    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ñ–∞–π–ª—ñ–≤:', error);
      setSaveStatus('‚ùå –ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ñ–∞–π–ª—ñ–≤');
      setTimeout(() => setSaveStatus(''), 3000);
    } finally {
      setUploading(false);
    }
  };

  const removeDocument = (index) => {
    setEditingSection(prev => ({
      ...prev,
      documents: prev.documents.filter((_, i) => i !== index)
    }));
  };

  const handleSave = async () => {
    if (!editingSection) return;

    try {
      await transparencyService.update(editingSection.section_type, {
        title: editingSection.title,
        content: editingSection.content,
        documents: editingSection.documents || []
      });

      setSaveStatus('‚úÖ –ó–±–µ—Ä–µ–∂–µ–Ω–æ —É—Å–ø—ñ—à–Ω–æ!');
      setTimeout(() => setSaveStatus(''), 3000);

      // –û–Ω–æ–≤–ª—é—î–º–æ —Å–ø–∏—Å–æ–∫
      loadSections();
      setEditingSection(null);
    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–±–µ—Ä–µ–∂–µ–Ω–Ω—è:', error);
      setSaveStatus('‚ùå –ü–æ–º–∏–ª–∫–∞ –∑–±–µ—Ä–µ–∂–µ–Ω–Ω—è');
      setTimeout(() => setSaveStatus(''), 3000);
    }
  };

  const handleCancel = () => {
    setEditingSection(null);
    setSaveStatus('');
  };

  if (loading) return <div className="loading">–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...</div>;

  return (
    <div className="transparency-management">
      <div className="page-header">
        <h1>–£–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è —Ä–æ–∑–¥—ñ–ª–æ–º "–ü—Ä–æ–∑–æ—Ä—ñ—Å—Ç—å"</h1>
        <p>–†–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è –∫–æ–Ω—Ç–µ–Ω—Ç—É –¥–ª—è —Ä–æ–∑–¥—ñ–ª—ñ–≤ –ø—Ä–æ–∑–æ—Ä–æ—Å—Ç—ñ —Ç–∞ —ñ–Ω—Ñ–æ—Ä–º–∞—Ü—ñ–π–Ω–æ—ó –≤—ñ–¥–∫—Ä–∏—Ç–æ—Å—Ç—ñ</p>
      </div>

      {saveStatus && (
        <div className={`alert ${saveStatus.includes('‚úÖ') ? 'alert-success' : 'alert-danger'}`}>
          {saveStatus}
        </div>
      )}

      {editingSection ? (
        <div className="editor-section">
          <div className="editor-header">
            <h2>–†–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è: {editingSection.title}</h2>
            <div className="editor-actions">
              <button className="btn btn-secondary" onClick={handleCancel}>
                –°–∫–∞—Å—É–≤–∞—Ç–∏
              </button>
              <button className="btn btn-primary" onClick={handleSave}>
                –ó–±–µ—Ä–µ–≥—Ç–∏ –∑–º—ñ–Ω–∏
              </button>
            </div>
          </div>

          <div className="content-editor">
            <label>–ö–æ–Ω—Ç–µ–Ω—Ç:</label>
            <RichTextEditor
              value={editingSection.content || ''}
              onChange={handleContentChange}
              height="400px"
            />
          </div>

          <div className="documents-section">
            <label>–î–æ–∫—É–º–µ–Ω—Ç–∏:</label>
            <div className="documents-upload">
              <input
                type="file"
                multiple
                onChange={handleFileUpload}
