import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate
} from "react-router-dom";

import Login from "./components/auth/Login";
import Dashboard from "./components/dashboard/Dashboard";
import Editor from "./components/editor/Editor";
import VisualEditor from "./components/visual-editor/VisualEditor";
import SitePage from "./components/site/SitePage";

import NewsList from "./components/news/NewsList";
import NewsItem from "./components/news/NewsItem";
import NewsManagement from "./components/admin/NewsManagement";
import NewsEditor from "./components/admin/NewsEditor";

import Home from "./components/static/Home";
import Departments from "./components/static/Departments";
import About from "./components/static/About";
import Contacts from "./components/static/Contacts";

import { authService } from "./services/authService";
import "./App.css";

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(null);

  useEffect(() => {
    const checkAuth = () => {
      const authenticated = authService.isAuthenticated();
      setIsAuthenticated(authenticated);
    };
    checkAuth();
  }, []);

  if (isAuthenticated === null) {
    return (
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          height: "100vh",
          background: "linear-gradient(135deg, #667eea 0%, #764ba2 100%)"
        }}
      >
        <div
          style={{
            background: "white",
            padding: "3rem",
            borderRadius: "20px",
            boxShadow: "0 20px 40px rgba(0,0,0,0.2)",
            textAlign: "center",
            minWidth: "350px",
            display: "flex",
            flexDirection: "column",
            alignItems: "center"
          }}
        >
          <img
            src="/img/logo_nav.png"
            alt="–ê–Ω—Ç–∞—Ä–µ—Å"
            style={{
              height: "80px",
              width: "auto",
              borderRadius: "12px",
              marginBottom: "1rem"
            }}
          />
          <h3 style={{ color: "#2c3e50", fontWeight: "600" }}>
            –®–∫–æ–ª–∞ –º–∏—Å—Ç–µ—Ü—Ç–≤ ¬´–ê–Ω—Ç–∞—Ä–µ—Å¬ª
          </h3>
          <p style={{ color: "#7f8c8d", fontSize: "1rem" }}>–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...</p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          {/* üîπ –°—Ç–∞—Ç–∏—á–Ω—ñ —Å—Ç–æ—Ä—ñ–Ω–∫–∏ */}
          <Route path="/site/home" element={<Home />} />
          <Route path="/site/departments" element={<Departments />} />
          <Route path="/site/about" element={<About />} />
          <Route path="/site/contacts" element={<Contacts />} />

          {/* üîπ –î–∏–Ω–∞–º—ñ—á–Ω—ñ —Å—Ç–æ—Ä—ñ–Ω–∫–∏ */}
          <Route path="/site/transparency" element={<SitePage />} />
          <Route path="/site/gallery" element={<SitePage />} />
          <Route path="/site/events" element={<SitePage />} />

          {/* üîπ –ù–æ–≤–∏–Ω–∏ */}
          <Route path="/site/news" element={<NewsList />} />
          <Route path="/site/news/:id" element={<NewsItem />} />

          {/* üîπ –ê–¥–º—ñ–Ω–∫–∞ —Ç–∞ –ª–æ–≥—ñ–Ω */}
          <Route
            path="/login"
            element={
              !isAuthenticated ? (
                <Login />
              ) : (
                <Navigate to="/admin/dashboard" replace />
              )
            }
          />

          <Route
            path="/admin/*"
            element={
              isAuthenticated ? <AdminLayout /> : <Navigate to="/login" replace />
            }
          />

