// Конфігурація для різних редакторів
export const editorConfig = {
  // TinyMCE конфігурація
  tinymce: {
    apiKey: "no-api-key", // Без ключа (буде водяний знак) або отримайте безкоштовний на tinymce.com
    init: {
      height: 500,
      menubar: true,
      plugins: [
        'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
        'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
        'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'
      ],
      toolbar: 'undo redo | blocks | bold italic underline | ' +
        'alignleft aligncenter alignright alignjustify | ' +
        'bullist numlist outdent indent | link image media | ' +
        'forecolor backcolor | table | code | fullscreen | help',
      content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:14px }',
      images_upload_handler: async (blobInfo) => {
        // Функція для завантаження зображень
        return new Promise((resolve, reject) => {
          // Тут буде логіка завантаження на сервер
          const formData = new FormData();
          formData.append('image', blobInfo.blob(), blobInfo.filename());

          fetch('http://localhost:5000/api/upload', {
            method: 'POST',
            body: formData
          })
          .then(response => response.json())
          .then(data => resolve(data.location))
          .catch(() => reject('Upload failed'));
        });
      }
    }
  },

  // CKEditor конфігурація (заготівка для майбутнього)
  ckeditor: {
    // Буде додано пізніше
  }
};

// Поточний активний редактор
export const ACTIVE_EDITOR = 'tinymce';
