import React from 'react';

const EditorToolbar = ({ isEditMode, onToggleEditMode, onSave, onExit, onAddImage, loading }) => {
  return (
    <div className="visual-editor-toolbar">
      <div className="toolbar-left">
        <h2>üìù –í—ñ–∑—É–∞–ª—å–Ω–∏–π —Ä–µ–¥–∞–∫—Ç–æ—Ä</h2>
        <span className="mode-indicator">
          {isEditMode ? 'üî¥ –†–µ–∂–∏–º —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è' : 'üü¢ –†–µ–∂–∏–º –ø–µ—Ä–µ–≥–ª—è–¥—É'}
        </span>
      </div>

      <div className="toolbar-center">
        <button 
          onClick={onToggleEditMode}
          className={`btn-mode ${isEditMode ? 'preview' : 'edit'}`}
        >
          {isEditMode ? 'üëÅÔ∏è –ü–µ—Ä–µ–≥–ª—è–¥' : '‚úèÔ∏è –†–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è'}
        </button>

        {isEditMode && (
          <>
            <button 
              onClick={onAddImage}
              className="btn-add-image"
            >
              üñºÔ∏è –î–æ–¥–∞—Ç–∏ –∑–æ–±—Ä–∞–∂–µ–Ω–Ω—è
            </button>

            <button 
              onClick={onSave} 
              disabled={loading}
              className="btn-save-main"
            >
              {loading ? 'üíæ –ó–±–µ—Ä–µ–∂–µ–Ω–Ω—è...' : 'üíæ –ó–±–µ—Ä–µ–≥—Ç–∏ —Å—Ç–æ—Ä—ñ–Ω–∫—É'}
            </button>
          </>
        )}
      </div>

      <div className="toolbar-right">
        <button onClick={onExit} className="btn-exit">
          ‚Üê –î–æ –ø–∞–Ω–µ–ª—ñ —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è
        </button>
      </div>
    </div>
  );
};

export default EditorToolbar;
