import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/auth/Login';
import Dashboard from './components/dashboard/Dashboard';
import Editor from './components/editor/Editor';
import { authService } from './services/authService';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(null);

  useEffect(() => {
    // Перевіряємо чи користувач авторизований при завантаженні
    const checkAuth = () => {
      const authenticated = authService.isAuthenticated();
      setIsAuthenticated(authenticated);
    };

    checkAuth();
  }, []);

  // Показуємо завантаження поки не визначили стан аутентифікації
  if (isAuthenticated === null) {
    return (
      <div style={{ 
        display: 'flex', 
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh' 
      }}>
        <div>Завантаження...</div>
      </div>
    );
  }

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route 
            path="/login" 
            element={
              !isAuthenticated ? <Login /> : <Navigate to="/admin/dashboard" />
            } 
          />
          <Route 
            path="/admin/*" 
            element={
              isAuthenticated ? <AdminLayout /> : <Navigate to="/login" />
            } 
          />
          <Route path="/" element={<Navigate to={isAuthenticated ? "/admin/dashboard" : "/login"} />} />
        </Routes>
      </div>
    </Router>
  );
}

// Компонент для захищених маршрутів адмін-панелі
const AdminLayout = () => {
  const handleLogout = () => {
    authService.logout();
    window.location.href = '/login';
  };

  return (
    <div>
      {/* Простий хедер для адмінки */}
      <header style={{
        backgroundColor: '#1f2937',
        color: 'white',
        padding: '1rem 2rem',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <h1 style={{ margin: 0, fontSize: '1.5rem' }}>Адмін-панель школи</h1>
        <button
          onClick={handleLogout}
          style={{
            backgroundColor: '#ef4444',
            color: 'white',
            border: 'none',
            padding: '0.5rem 1rem',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          Вийти
        </button>
      </header>

      <Routes>
        <Route path="dashboard" element={<Dashboard />} />
        <Route path="editor/:pageName" element={<Editor />} />
        <Route path="" element={<Navigate to="dashboard" />} />
      </Routes>
    </div>
  );
};

export default App;
