import React from 'react';

const About = () => {
  return (
    <div className="about-page" style={{ padding: '2rem', maxWidth: '1200px', margin: '0 auto' }}>
      <h1>Про нашу школу</h1>
      <div className="about-content" style={{ display: 'flex', gap: '2rem', alignItems: 'center', marginTop: '2rem' }}>
        <img 
          src="/img/school-building.jpg" 
          alt="Будівля школи" 
          style={{ width: '400px', borderRadius: '10px' }}
        />
        <div>
          <h2>Історія школи</h2>
          <p>
            Школа мистецтв «Антарес» була заснована в 1995 році та з того часу 
            стала одним з провідних центрів мистецької освіти в нашому регіоні.
          </p>
          <h2>Наша місія</h2>
          <p>
            Ми прагнемо розвивати творчий потенціал кожної дитини, навчаючи її 
            не лише технікам мистецтва, але й розвиваючи критичне мислення, 
            креативність та впевненість у собі.
          </p>
        </div>
      </div>
    </div>
  );
};

export default About;
