import React from 'react';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  // –°—Ç–æ—Ä—ñ–Ω–∫–∏, —è–∫—ñ –±–µ—Ä—É—Ç—å –¥–∞–Ω—ñ –∑ –±–∞–∑–∏
  const dynamicPages = [
    { 
      name: 'üì∞ –ù–æ–≤–∏–Ω–∏', 
      path: 'news', 
      description: '–ü–æ–≤–Ω–µ –∫–µ—Ä—É–≤–∞–Ω–Ω—è –Ω–æ–≤–∏–Ω–∞–º–∏ —à–∫–æ–ª–∏',
      adminPath: 'news',
      type: 'crm',
      color: '#9b59b6'
    },
    { 
      name: 'üñºÔ∏è –§–æ—Ç–æ/–í—ñ–¥–µ–æ', 
      path: 'gallery', 
      description: '–†–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è –≥–∞–ª–µ—Ä–µ—ó —Ñ–æ—Ç–æ —Ç–∞ –≤—ñ–¥–µ–æ –º–∞—Ç–µ—Ä—ñ–∞–ª—ñ–≤',
      adminPath: 'visual-editor/gallery',
      type: 'editor',
      color: '#e74c3c'
    },
    { 
      name: 'üìä –ü—Ä–æ–∑–æ—Ä—ñ—Å—Ç—å', 
      path: 'transparency', 
      description: '–£–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è –¥–æ–∫—É–º–µ–Ω—Ç–∞–º–∏ —Ç–∞ –∑–≤—ñ—Ç–Ω—ñ—Å—Ç—é —à–∫–æ–ª–∏',
      adminPath: 'visual-editor/transparency',
      type: 'editor',
      color: '#3498db'
    }
  ];

  return (
    <div style={{ padding: '2rem', maxWidth: '1200px', margin: '0 auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h1 style={{ fontSize: '2.5rem', color: '#2c3e50', margin: 0 }}>
          –ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è —Å–∞–π—Ç–æ–º
        </h1>
        <a 
          href="/site/home" 
          target="_blank"
          rel="noopener noreferrer"
          style={{
            backgroundColor: '#27ae60',
            color: 'white',
            padding: '0.75rem 1.5rem',
            textDecoration: 'none',
            borderRadius: '5px',
            fontWeight: 'bold'
          }}
        >
          üëÅÔ∏è –ü–µ—Ä–µ–≥–ª—è–¥ —Å–∞–π—Ç—É
        </a>
      </div>

      {/* –°–µ–∫—Ü—ñ—è –∫–µ—Ä—É–≤–∞–Ω–Ω—è –¥–∏–Ω–∞–º—ñ—á–Ω–∏–º –∫–æ–Ω—Ç–µ–Ω—Ç–æ–º */}
      <div style={{ marginBottom: '3rem' }}>
        <h2 style={{ 
          color: '#2c3e50', 
          marginBottom: '1.5rem', 
          borderBottom: '2px solid #3498db', 
          paddingBottom: '0.5rem',
          display: 'flex',
          alignItems: 'center',
          gap: '0.5rem'
        }}>
          üìä –ö–µ—Ä—É–≤–∞–Ω–Ω—è –¥–∏–Ω–∞–º—ñ—á–Ω–∏–º –∫–æ–Ω—Ç–µ–Ω—Ç–æ–º
        </h2>
        <p style={{ color: '#7f8c8d', marginBottom: '2rem' }}>
          –°—Ç–æ—Ä—ñ–Ω–∫–∏, —è–∫—ñ –æ—Ç—Ä–∏–º—É—é—Ç—å –¥–∞–Ω—ñ –∑ –±–∞–∑–∏ –¥–∞–Ω–∏—Ö —Ç–∞ –º–æ–∂—É—Ç—å —Ä–µ–¥–∞–≥—É–≤–∞—Ç–∏—Å—è
        </p>

        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
          gap: '1.5rem'
        }}>
          {dynamicPages.map((page) => (
            <div key={page.path} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>

              {page.type === 'crm' ? (
                // CRM —Å–∏—Å—Ç–µ–º–∞ –¥–ª—è –Ω–æ–≤–∏–Ω
                <Link
                  to={`/admin/${page.adminPath}`}
                  style={{
                    background: 'white',
                    padding: '2rem',
                    borderRadius: '10px',
                    boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                    textDecoration: 'none',
                    color: 'inherit',
                    border: `2px solid ${page.color}`,
                    transition: 'all 0.3s ease',
                    display: 'block'
                  }}
                  onMouseOver={(e) => {
                    e.currentTarget.style.transform = 'translateY(-5px)';
                    e.currentTarget.style.boxShadow = '0 8px 25px rgba(0,0,0,0.15)';
                  }}
                  onMouseOut={(e) => {
                    e.currentTarget.style.transform = 'translateY(0)';
                    e.currentTarget.style.boxShadow = '0 4px 6px rgba(0,0,0,0.1)';
                  }}
                >
                  <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
                    <span style={{ fontSize: '2rem' }}>{page.name.split(' ')[0]}</span>
                    <h3 style={{ 
                      fontSize: '1.5rem', 
                      margin: 0,
                      color: '#2c3e50'
                    }}>
                      {page.name.split(' ').slice(1).join(' ')}
                    </h3>
                  </div>
                  <p style={{ 
                    color: '#7f8c8d',
                    margin: 0,
                    lineHeight: '1.5'
                  }}>
                    {page.description}
                  </p>
                  <div style={{
                    marginTop: '1rem',
                    padding: '0.5rem',
                    background: '#f4ecf7',
                    borderRadius: '5px',
                    fontSize: '0.9rem',
                    color: '#8e44ad',
                    textAlign: 'center'
                  }}>
                    ‚öôÔ∏è –ü–æ–≤–Ω–∞ CRM —Å–∏—Å—Ç–µ–º–∞
                  </div>
                </Link>
              ) : (
                // –í—ñ–∑—É–∞–ª—å–Ω–∏–π —Ä–µ–¥–∞–∫—Ç–æ—Ä –¥–ª—è —ñ–Ω—à–∏—Ö —Å—Ç–æ—Ä—ñ–Ω–æ–∫
                <Link
                  to={`/admin/${page.adminPath}`}
                  style={{
                    background: 'white',
                    padding: '2rem',
                    borderRadius: '10px',
                    boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                    textDecoration: 'none',
                    color: 'inherit',
