import React, { useState, useRef, useEffect } from 'react';

const Modal = ({ dept, onClose }) => {
  const dialogRef = useRef(null);

  // Закривати по ESC та кліку по бекдропу
  useEffect(() => {
    const onKey = (e) => e.key === 'Escape' && onClose();
    document.addEventListener('keydown', onKey);
    return () => document.removeEventListener('keydown', onKey);
  }, [onClose]);

  if (!dept) return null;

  return (
    <div
      onClick={(e) => {
        // клік по бекдропу
        if (e.target === e.currentTarget) onClose();
      }}
      style={{
        position: 'fixed',
        inset: 0,
        background: 'rgba(0,0,0,0.4)',
        display: 'grid',
        placeItems: 'center',
        zIndex: 1000
      }}
      role="dialog"
      aria-modal="true"
      aria-labelledby="dept-title"
      ref={dialogRef}
    >
      <div style={{
        width: 'min(900px, 92vw)',
        maxHeight: '85vh',
        overflow: 'auto',
        background: '#fff',
        borderRadius: 16,
        boxShadow: '0 20px 60px rgba(0,0,0,.25)',
        padding: '24px 28px'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', gap: 12, alignItems: 'center', marginBottom: 8 }}>
          <h2 id="dept-title" style={{ margin: 0, color: '#1f2937' }}>{dept.name}</h2>
          <button
            onClick={onClose}
            style={{
              border: '1px solid #e5e7eb',
              padding: '6px 10px',
              borderRadius: 8,
              background: '#f9fafb',
              cursor: 'pointer'
            }}
          >
            Закрити
          </button>
        </div>

        <p style={{ marginTop: 0, color: '#374151', lineHeight: 1.6 }}>{dept.longText}</p>

        <h3 style={{ marginTop: 24, marginBottom: 12 }}>Викладачі</h3>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(220px, 1fr))',
          gap: 16
        }}>
          {dept.teachers.map((t, i) => (
            <div key={i} style={{
              display: 'flex',
              gap: 12,
              alignItems: 'center',
              padding: 12,
              border: '1px solid #eee',
              borderRadius: 12
            }}>
              <img
                src={t.photo || '/img/teacher-placeholder.jpg'}
                alt={t.name}
                style={{ width: 56, height: 56, borderRadius: '50%', objectFit: 'cover' }}
              />
              <div>
                <div style={{ fontWeight: 600 }}>{t.name}</div>
                <div style={{ fontSize: 14, color: '#6b7280' }}>{t.title}</div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

