import React, { useState } from 'react';
import { Editor } from '@tinymce/tinymce-react';

const EditableElement = ({ 
  elementId, 
  content, 
  isEditing, 
  onEditStart, 
  onSave, 
  onCancel,
  tag = 'div',
  className = ''
}) => {
  const [currentContent, setCurrentContent] = useState(content);

  const handleSave = () => {
    onSave(currentContent);
  };

  const handleCancel = () => {
    setCurrentContent(content);
    onCancel();
  };

  // –ü—Ä–æ—Å—Ç–∏–π –ø–µ—Ä–µ–≥–ª—è–¥ –∫–æ–Ω—Ç–µ–Ω—Ç—É
  const renderContent = () => {
    const Tag = tag;
    return (
      <Tag 
        className={`editable-element ${className}`}
        onClick={onEditStart}
        dangerouslySetInnerHTML={{ __html: content }}
      />
    );
  };

  // –†–µ–¥–∞–∫—Ç–æ—Ä –∑ TinyMCE
  const renderEditor = () => {
    return (
      <div className={`editable-element-editor ${className}`}>
        <div className="editor-toolbar">
          <button onClick={handleSave} className="btn-save">üíæ –ó–±–µ—Ä–µ–≥—Ç–∏</button>
          <button onClick={handleCancel} className="btn-cancel">‚ùå –°–∫–∞—Å—É–≤–∞—Ç–∏</button>
        </div>

        <Editor
          apiKey="no-api-key" // –ë–µ–∑ –∫–ª—é—á–∞ –¥–ª—è —Ç–µ—Å—Ç—É–≤–∞–Ω–Ω—è
          value={currentContent}
          onEditorChange={setCurrentContent}
          init={{
            height: 500,
            menubar: true,
            plugins: [
              'advlist', 'autolink', 'lists', 'link', 'image', 'charmap', 'preview',
              'anchor', 'searchreplace', 'visualblocks', 'code', 'fullscreen',
              'insertdatetime', 'media', 'table', 'code', 'help', 'wordcount'
            ],
            toolbar: 'undo redo | blocks | bold italic underline | ' +
              'alignleft aligncenter alignright alignjustify | ' +
              'bullist numlist outdent indent | link image | ' +
              'forecolor backcolor | table | code | fullscreen | help',
            content_style: 'body { font-family:Helvetica,Arial,sans-serif; font-size:16px; line-height:1.6; } h1, h2, h3 { margin: 20px 0 10px 0; } p { margin: 10px 0; }',
            images_upload_handler: async (blobInfo) => {
              return new Promise((resolve, reject) => {
                // –ü—Ä–æ—Å—Ç–∏–π –æ–±—Ä–æ–±–Ω–∏–∫ –¥–ª—è –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —á–µ—Ä–µ–∑ –Ω–∞—à—É —Å–∏—Å—Ç–µ–º—É
                const formData = new FormData();
                formData.append('image', blobInfo.blob(), blobInfo.filename());

                fetch('http://localhost:5000/api/upload', {
                  method: 'POST',
                  body: formData
                })
                .then(response => response.json())
                .then(data => resolve(data.url))
                .catch(() => reject('Upload failed'));
              });
            }
          }}
        />
      </div>
    );
  };

  return isEditing ? renderEditor() : renderContent();
};

export default EditableElement;
