import React, { useState, useEffect } from 'react';
import './ImageUploader.css';

const ImageUploader = ({ onInsert, onClose }) => {
  const [uploading, setUploading] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadedImages, setUploadedImages] = useState([]);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // –ó–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏ —ñ—Å–Ω—É—é—á—ñ –∑–æ–±—Ä–∞–∂–µ–Ω–Ω—è –ø—Ä–∏ –≤—ñ–¥–∫—Ä–∏—Ç—Ç—ñ
  useEffect(() => {
    loadExistingImages();
  }, []);

  const loadExistingImages = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/upload');
      const data = await response.json();
      if (Array.isArray(data)) {
        setUploadedImages(data);
      }
    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —ñ—Å–Ω—É—é—á–∏—Ö –∑–æ–±—Ä–∞–∂–µ–Ω—å:', error);
    }
  };

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      // –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ —Ç–∏–ø—É —Ñ–∞–π–ª—É
      if (!file.type.startsWith('image/')) {
        setError('–ë—É–¥—å –ª–∞—Å–∫–∞, –≤–∏–±–µ—Ä—ñ—Ç—å —Ñ–∞–π–ª –∑–æ–±—Ä–∞–∂–µ–Ω–Ω—è');
        return;
      }

      // –ü–µ—Ä–µ–≤—ñ—Ä–∫–∞ —Ä–æ–∑–º—ñ—Ä—É —Ñ–∞–π–ª—É (10MB)
      if (file.size > 10 * 1024 * 1024) {
        setError('–§–∞–π–ª –∑–∞–Ω–∞–¥—Ç–æ –≤–µ–ª–∏–∫–∏–π. –ú–∞–∫—Å–∏–º–∞–ª—å–Ω–∏–π —Ä–æ–∑–º—ñ—Ä: 10MB');
        return;
      }

      setSelectedFile(file);
      setError('');
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setError('–ë—É–¥—å –ª–∞—Å–∫–∞, –≤–∏–±–µ—Ä—ñ—Ç—å —Ñ–∞–π–ª');
      return;
    }

    setUploading(true);
    setError('');
    setSuccess('');

    try {
      const formData = new FormData();
      formData.append('image', selectedFile);

      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      if (data.success) {
        setUploadedImages(prev => [data, ...prev]);
        setSelectedFile(null);
        setSuccess('–ó–æ–±—Ä–∞–∂–µ–Ω–Ω—è —É—Å–ø—ñ—à–Ω–æ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–æ!');

        // –û—á–∏—Å—Ç–∏—Ç–∏ input —Ñ–∞–π–ª—É
        const fileInput = document.getElementById('file-input');
        if (fileInput) fileInput.value = '';

        // –°—Ö–æ–≤–∞—Ç–∏ –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è –ø—Ä–æ —É—Å–ø—ñ—Ö —á–µ—Ä–µ–∑ 2 —Å–µ–∫—É–Ω–¥–∏
        setTimeout(() => setSuccess(''), 2000);
      } else {
        setError(data.error || '–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è –∑–æ–±—Ä–∞–∂–µ–Ω–Ω—è');
      }
    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è:', error);
      setError('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è –∑–æ–±—Ä–∞–∂–µ–Ω–Ω—è. –ü–µ—Ä–µ–≤—ñ—Ä—Ç–µ –ø—ñ–¥–∫–ª—é—á–µ–Ω–Ω—è –¥–æ —Å–µ—Ä–≤–µ—Ä–∞.');
    } finally {
      setUploading(false);
    }
  };

  const handleImageClick = (imageUrl) => {
    onInsert(imageUrl);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      setSelectedFile(files[0]);
    }
  };

  return (
    <div className="image-uploader-overlay">
      <div className="image-uploader-modal">
        <div className="uploader-header">
          <h3>üñºÔ∏è –î–æ–¥–∞—Ç–∏ –∑–æ–±—Ä–∞–∂–µ–Ω–Ω—è</h3>
          <button className="close-btn" onClick={onClose}>√ó</button>
        </div>

        {error && (
          <div className="error-message">
            {error}
          </div>
        )}

        {success && (
          <div className="success-message">
            {success}
          </div>
        )}

        <div 
          className="upload-section"
          onDragOver={handleDragOver}
          onDrop={handleDrop}
        >
          <input
            id="file-input"
            type="file"
            accept="image/*"
            onChange={handleFileSelect}
            className="file-input"
          />

          {uploading ? (
            <div className="uploading-indicator">
              <div className="uploading-spinner"></div>
              –ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...
            </div>
          ) : (
            <button
              onClick={handleUpload}
              disabled={!selectedFile}
              className="upload-btn"
            >
              üì§ –ó–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏
            </button>
          )}
        </div>

        {selectedFile && !uploading && (
          <div style={{ 
            marginBottom: '15px', 
            padding: '10px', 
            background: '#e8f4fd', 
            borderRadius: '6px',
            fontSize: '14px'
          }}>
            üìé –í–∏–±—Ä–∞–Ω–æ: <strong>{selectedFile.name}</strong> 
            ({Math.round(selectedFile.size / 1024)} KB)
          </div>
        )}

        <div className="images-grid">
          {uploadedImages.length > 0 ? (
            uploadedImages.map((image, index) => (
              <div
                key={index}
                className="image-item"
                onClick={() => handleImageClick(image.url)}
                title="–ù–∞—Ç–∏—Å–Ω—ñ—Ç—å –¥–ª—è –≤—Å—Ç–∞–≤–∫–∏ –≤ –∫–æ–Ω—Ç–µ–Ω—Ç"
              >
                <img src={image.url} alt={`–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–µ ${index + 1}`} />
                <div className="image-overlay">–í—Å—Ç–∞–≤–∏—Ç–∏</div>
              </div>
            ))
          ) : (
            <div style={{ 
              gridColumn: '1 / -1', 
              textAlign: 'center', 
              color: '#7f8c8d',
              padding: '40px'
            }}>
              üñºÔ∏è –ù–µ–º–∞—î –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–∏—Ö –∑–æ–±—Ä–∞–∂–µ–Ω—å
            </div>
          )}
        </div>

        <div className="uploader-footer">
          <button onClick={onClose} className="cancel-btn">
            –°–∫–∞—Å—É–≤–∞—Ç–∏
          </button>
        </div>
      </div>
    </div>
  );
};

export default ImageUploader;
