import React, { useState } from 'react';
import { contentService } from '../../services/contentService';
import './ImageUploader.css';

const ImageUploader = ({ onInsert, onClose }) => {
  const [uploading, setUploading] = useState(false);
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploadedImages, setUploadedImages] = useState([]);

  const handleFileSelect = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    setUploading(true);
    try {
      const formData = new FormData();
      formData.append('image', selectedFile);

      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      if (data.success) {
        setUploadedImages(prev => [data, ...prev]);
        setSelectedFile(null);
        // Очистити input файлу
        document.getElementById('file-input').value = '';
      }
    } catch (error) {
      console.error('Помилка завантаження:', error);
      alert('Помилка завантаження зображення');
    } finally {
      setUploading(false);
    }
  };

  const handleImageClick = (imageUrl) => {
    onInsert(imageUrl);
  };

  return (
    <div className="image-uploader-overlay">
      <div className="image-uploader-modal">
        <div className="uploader-header">
          <h3>Додати зображення</h3>
          <button className="close-btn" onClick={onClose}>×</button>
        </div>

        <div className="upload-section">
          <input
            id="file-input"
            type="file"
            accept="image/*"
            onChange={handleFileSelect}
            className="file-input"
          />
          <button
            onClick={handleUpload}
            disabled={!selectedFile || uploading}
            className="upload-btn"
          >
            {uploading ? 'Завантаження...' : 'Завантажити'}
          </button>
        </div>

        <div className="images-grid">
          {uploadedImages.map((image, index) => (
            <div
              key={index}
              className="image-item"
              onClick={() => handleImageClick(image.url)}
            >
              <img src={image.url} alt={`Завантажене ${index}`} />
              <div className="image-overlay">Вставити</div>
            </div>
          ))}
        </div>

        <div className="uploader-footer">
          <button onClick={onClose} className="cancel-btn">Скасувати</button>
        </div>
      </div>
    </div>
  );
};

export default ImageUploader;
