import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import SiteLayout from './SiteLayout';
import { contentService } from '../../services/contentService';
import './SitePage.css';

// Імпортуємо статичні компоненти
import Home from '../static/Home';
import About from '../static/About';
import Departments from '../static/Departments';
import Contacts from '../static/Contacts';
import NewsList from '../news/NewsList';
import NewsItem from '../news/NewsItem';

const SitePage = () => {
  const { pageName } = useParams();
  const [content, setContent] = useState('');
  const [loading, setLoading] = useState(false);

  // Визначаємо, для яких сторінок використовувати динамічний контент з бази
  const dynamicPages = ['gallery', 'transparency'];

  useEffect(() => {
    // Якщо сторінка динамічна, завантажуємо контент з бази
    if (dynamicPages.includes(pageName)) {
      loadContent();
    }
  }, [pageName]);

  const loadContent = async () => {
    setLoading(true);
    try {
      const response = await contentService.getContent(pageName);
      setContent(response.data.content || '');
    } catch (error) {
      console.error('Помилка завантаження контенту:', error);
      setContent('<p>Контент не знайдено</p>');
    } finally {
      setLoading(false);
    }
  };

  // Функція для рендерингу контенту на основі pageName
  const renderContent = () => {
    if (dynamicPages.includes(pageName)) {
      // Динамічні сторінки (галерея, прозорість) - з бази даних
      if (loading) {
        return <div style={{ padding: '2rem', textAlign: 'center' }}>Завантаження...</div>;
      }
      return <div dangerouslySetInnerHTML={{ __html: content }} />;
    } else {
      // Статичні сторінки - з компонентів
      switch (pageName) {
        case 'home':
          return <Home />;
        case 'about':
          return <About />;
        case 'departments':
          return <Departments />;
        case 'contacts':
          return <Contacts />;
        case 'news':
          return <NewsList />;
        default:
          return (
            <div style={{ padding: '2rem', textAlign: 'center' }}>
              <h1>Сторінку не знайдено</h1>
              <p>Сторінка "{pageName}" не існує.</p>
              <a href="/site/home">Повернутися на головну</a>
            </div>
          );
      }
    }
  };

  return (
    <SiteLayout>
      {renderContent()}
    </SiteLayout>
  );
};

// Окремий компонент для сторінки новини
export const NewsPage = () => {
  const { id } = useParams();

  if (id) {
    return (
      <SiteLayout>
        <NewsItem />
      </SiteLayout>
    );
  }

  return (
    <SiteLayout>
      <NewsList />
    </SiteLayout>
  );
};

export default SitePage;
