import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import galleryService from '../../services/galleryService';
import './GalleryManagement.css';

const GalleryManagement = () => {
  const [images, setImages] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showForm, setShowForm] = useState(false);
  const [uploadMethod, setUploadMethod] = useState('link'); // 'link' –∞–±–æ 'file'
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    category: 'general',
    image_url: '' // –î–ª—è –ø–æ—Å–∏–ª–∞–Ω—å
  });
  const [selectedFile, setSelectedFile] = useState(null);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    loadImages();
  }, []);

  const loadImages = async () => {
    setLoading(true);
    try {
      const response = await galleryService.getGalleryImages();
      setImages(response.images || []);
    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è –∑–æ–±—Ä–∞–∂–µ–Ω—å:', error);
      alert('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è –∑–æ–±—Ä–∞–∂–µ–Ω—å');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setSelectedFile(file);
      if (!formData.title) {
        const fileName = file.name.split('.')[0];
        setFormData(prev => ({ ...prev, title: fileName }));
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (uploadMethod === 'file' && !selectedFile) {
      alert('–ë—É–¥—å –ª–∞—Å–∫–∞, –≤–∏–±–µ—Ä—ñ—Ç—å —Ñ–∞–π–ª');
      return;
    }

    if (uploadMethod === 'link' && !formData.image_url.trim()) {
      alert('–ë—É–¥—å –ª–∞—Å–∫–∞, –≤–≤–µ–¥—ñ—Ç—å –ø–æ—Å–∏–ª–∞–Ω–Ω—è –Ω–∞ —Ñ–æ—Ç–æ');
      return;
    }

    if (!formData.title.trim()) {
      alert('–ë—É–¥—å –ª–∞—Å–∫–∞, –≤–≤–µ–¥—ñ—Ç—å –∑–∞–≥–æ–ª–æ–≤–æ–∫');
      return;
    }

    setUploading(true);
    try {
      let imageUrl = '';

      if (uploadMethod === 'file') {
        // –ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ñ–∞–π–ª—É
        const submitFormData = new FormData();
        submitFormData.append('image', selectedFile);
        submitFormData.append('title', formData.title);
        submitFormData.append('description', formData.description);
        submitFormData.append('category', formData.category);

        const response = await galleryService.uploadGalleryImage(submitFormData);
        imageUrl = response.url || response.image_url;
      } else {
        // –í–∏–∫–æ—Ä–∏—Å—Ç–∞–Ω–Ω—è –ø–æ—Å–∏–ª–∞–Ω–Ω—è
        imageUrl = formData.image_url;

        // –ü–µ—Ä–µ—Ç–≤–æ—Ä—é—î–º–æ –ø–æ—Å–∏–ª–∞–Ω–Ω—è Google Drive –Ω–∞ –ø—Ä—è–º–µ
        const directLink = convertGoogleDriveLink(formData.image_url);

        const imageData = {
          title: formData.title,
          description: formData.description,
          image_url: directLink,
          category: formData.category
        };

        await galleryService.createGalleryImage(imageData);
      }

      // –û—á–∏—â–∞—î–º–æ —Ñ–æ—Ä–º—É
      setFormData({
        title: '',
        description: '',
        category: 'general',
        image_url: ''
      });
      setSelectedFile(null);
      setShowForm(false);

      // –û–Ω–æ–≤–ª—é—î–º–æ —Å–ø–∏—Å–æ–∫
      await loadImages();

      alert('–§–æ—Ç–æ —É—Å–ø—ñ—à–Ω–æ –¥–æ–¥–∞–Ω–æ –¥–æ –≥–∞–ª–µ—Ä–µ—ó!');
    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è:', error);
      alert('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ñ–æ—Ç–æ: ' + error.message);
    } finally {
      setUploading(false);
    }
  };

  // –§—É–Ω–∫—Ü—ñ—è –¥–ª—è –ø–µ—Ä–µ—Ç–≤–æ—Ä–µ–Ω–Ω—è –ø–æ—Å–∏–ª–∞–Ω–Ω—è Google Drive
  const convertGoogleDriveLink = (url) => {
    try {
      // –Ø–∫—â–æ —Ü–µ –≤–∂–µ –ø—Ä—è–º–µ –ø–æ—Å–∏–ª–∞–Ω–Ω—è –Ω–∞ –∑–æ–±—Ä–∞–∂–µ–Ω–Ω—è
      if (url.includes('drive.google.com/uc?id=') || url.includes('lh3.googleusercontent.com')) {
        return url;
      }

      // –Ø–∫—â–æ —Ü–µ –ø–æ—Å–∏–ª–∞–Ω–Ω—è –¥–ª—è –ø–µ—Ä–µ–≥–ª—è–¥—É
      if (url.includes('drive.google.com/file/d/')) {
        const match = url.match(/\/file\/d\/([^\/]+)/);
        if (match && match[1]) {
          return `https://drive.google.com/uc?id=${match[1]}`;
        }
      }

      // –Ø–∫—â–æ —Ü–µ –ø–æ—Å–∏–ª–∞–Ω–Ω—è –¥–ª—è —Å–ø—ñ–ª—å–Ω–æ–≥–æ –¥–æ—Å—Ç—É–ø—É
      if (url.includes('drive.google.com/open?id=')) {
        const match = url.match(/open\?id=([^&]+)/);
        if (match && match[1]) {
          return `https://drive.google.com/uc?id=${match[1]}`;
        }
      }

      return url; // –ü–æ–≤–µ—Ä—Ç–∞—î–º–æ –æ—Ä–∏–≥—ñ–Ω–∞–ª—å–Ω–µ –ø–æ—Å–∏–ª–∞–Ω–Ω—è, —è–∫—â–æ –Ω–µ –≤–¥–∞–ª–æ—Å—è –ø–µ—Ä–µ—Ç–≤–æ—Ä–∏—Ç–∏
    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –ø–µ—Ä–µ—Ç–≤–æ—Ä–µ–Ω–Ω—è –ø–æ—Å–∏–ª–∞–Ω–Ω—è:', error);
      return url;
    }
  };

  const handleDelete = async (imageId) => {
    if (!window.confirm('–í–∏ –≤–ø–µ–≤–Ω–µ–Ω—ñ, —â–æ —Ö–æ—á–µ—Ç–µ –≤–∏–¥–∞–ª–∏—Ç–∏ —Ü–µ —Ñ–æ—Ç–æ?')) {
      return;
    }

    try {
      await galleryService.deleteGalleryImage(imageId);
      setImages(images.filter(img => img.id !== imageId));
      alert('–§–æ—Ç–æ —É—Å–ø—ñ—à–Ω–æ –≤–∏–¥–∞–ª–µ–Ω–æ!');
    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –≤–∏–¥–∞–ª–µ–Ω–Ω—è:', error);
      alert('–ü–æ–º–∏–ª–∫–∞ –≤–∏–¥–∞–ª–µ–Ω–Ω—è —Ñ–æ—Ç–æ');
    }
  };

  const cancelForm = () => {
    setShowForm(false);
    setFormData({
      title: '',
      description: '',
      category: 'general',
      image_url: ''
    });
    setSelectedFile(null);
    setUploadMethod('link');
  };

  return (
    <div className="gallery-management">
      <div className="gallery-header">
        <div className="header-top">
          <h1>–ö–µ—Ä—É–≤–∞–Ω–Ω—è –≥–∞–ª–µ—Ä–µ—î—é</h1>
          <Link to="/site/gallery" target="_blank" className="view-site-btn">
            üëÅÔ∏è –ü–µ—Ä–µ–≥–ª—è–¥ –Ω–∞ —Å–∞–π—Ç—ñ
          </Link>
        </div>
        <p>–î–æ–¥–∞–≤–∞–π—Ç–µ —Ñ–æ—Ç–æ —á–µ—Ä–µ–∑ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ñ–∞–π–ª—ñ–≤ –∞–±–æ –ø–æ—Å–∏–ª–∞–Ω–Ω—è –∑ Google –î–∏—Å–∫—É</p>
      </div>

      {/* –ö–Ω–æ–ø–∫–∞ –¥–æ–¥–∞—Ç–∏ –Ω–æ–≤–µ —Ñ–æ—Ç–æ */}
      <div className="add-photo-section">
        <button 
          onClick={() => setShowForm(true)}
          className="add-photo-btn"
          disabled={showForm}
        >
          ‚ûï –î–æ–¥–∞—Ç–∏ –Ω–æ–≤–µ —Ñ–æ—Ç–æ
        </button>
      </div>

      {/* –§–æ—Ä–º–∞ –¥–æ–¥–∞–≤–∞–Ω–Ω—è —Ñ–æ—Ç–æ */}
      {showForm && (
        <div className="photo-form-overlay">
          <div className="photo-form">
            <div className="form-header">
              <h2>–î–æ–¥–∞—Ç–∏ –Ω–æ–≤–µ —Ñ–æ—Ç–æ</h2>
              <button onClick={cancelForm} className="close-btn">√ó</button>
            </div>

            {/* –í–∏–±—ñ—Ä –º–µ—Ç–æ–¥—É –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è */}
            <div className="upload-method-selector">
              <label>–°–ø–æ—Å—ñ–± –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è:</label>
              <div className="method-buttons">
                <button
                  type="button"
                  className={`method-btn ${uploadMethod === 'link' ? 'active' : ''}`}
                  onClick={() => setUploadMethod('link')}
                >
                  üìé –ü–æ—Å–∏–ª–∞–Ω–Ω—è –∑ Google –î–∏—Å–∫—É
                </button>
                <button
                  type="button"
                  className={`method-btn ${uploadMethod === 'file' ? 'active' : ''}`}
                  onClick={() => setUploadMethod('file')}
                >
                  üìÅ –ó–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏ —Ñ–∞–π–ª
                </button>
              </div>
            </div>

            <form onSubmit={handleSubmit}>
              {/* –ü–æ–ª–µ –¥–ª—è –ø–æ—Å–∏–ª–∞–Ω–Ω—è */}
              {uploadMethod === 'link' && (
                <div className="form-group">
                  <label>–ü–æ—Å–∏–ª–∞–Ω–Ω—è –Ω–∞ —Ñ–æ—Ç–æ –∑ Google –î–∏—Å–∫—É *</label>
                  <input
                    type="url"
                    name="image_url"
                    value={formData.image_url}
                    onChange={handleInputChange}
                    placeholder="https://drive.google.com/uc?id=FILE_ID"
                    className="text-input"
                    required={uploadMethod === 'link'}
                  />
                  <div className="help-text">
                    <p><strong>–Ø–∫ –æ—Ç—Ä–∏–º–∞—Ç–∏ –ø–æ—Å–∏–ª–∞–Ω–Ω—è:</strong></p>
                    <ol>
                      <li>–ó–∞–≤–∞–Ω—Ç–∞–∂—Ç–µ —Ñ–æ—Ç–æ –Ω–∞ Google –î–∏—Å–∫</li>
                      <li>–ù–∞—Ç–∏—Å–Ω—ñ—Ç—å –ø—Ä–∞–≤–æ—é –∫–Ω–æ–ø–∫–æ—é –Ω–∞ —Ñ–∞–π–ª ‚Üí "–ù–∞–¥–∞—Ç–∏ –¥–æ—Å—Ç—É–ø" ‚Üí "–î–æ—Å—Ç—É–ø –¥–ª—è –≤—Å—ñ—Ö, —Ö—Ç–æ –º–∞—î –ø–æ—Å–∏–ª–∞–Ω–Ω—è"</li>
                      <li>–°–∫–æ–ø—ñ—é–π—Ç–µ –ø–æ—Å–∏–ª–∞–Ω–Ω—è —Ç–∞ –≤—Å—Ç–∞–≤—Ç–µ —Å—é–¥–∏</li>
                    </ol>
                    <p><strong>–ü—Ä–∏–∫–ª–∞–¥ –ø–æ—Å–∏–ª–∞–Ω–Ω—è:</strong><br/>
                    <code>https://drive.google.com/uc?id=1ABC123def456...</code></p>
                  </div>
                </div>
              )}

              {/* –ü–æ–ª–µ –¥–ª—è –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ñ–∞–π–ª—É */}
              {uploadMethod === 'file' && (
                <div className="form-group">
                  <label>–§–æ—Ç–æ *</label>
                  <input
                    type="file"
                    accept="image/*"
                    onChange={handleFileSelect}
                    className="file-input"
                    required={uploadMethod === 'file'}
                  />
                  {selectedFile && (
                    <div className="file-preview">
                      <img src={URL.createObjectURL(selectedFile)} alt="Preview" />
                      <span>{selectedFile.name}</span>
                    </div>
                  )}
                </div>
              )}

              <div className="form-group">
                <label>–ó–∞–≥–æ–ª–æ–≤–æ–∫ *</label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleInputChange}
                  placeholder="–í–≤–µ–¥—ñ—Ç—å –∑–∞–≥–æ–ª–æ–≤–æ–∫ —Ñ–æ—Ç–æ"
                  className="text-input"
                  required
                />
              </div>

              <div className="form-group">
                <label>–û–ø–∏—Å (–Ω–µ–æ–±–æ–≤'—è–∑–∫–æ–≤–æ)</label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleInputChange}
                  placeholder="–í–≤–µ–¥—ñ—Ç—å –æ–ø–∏—Å —Ñ–æ—Ç–æ"
                  className="text-input"
                  rows="3"
                />
              </div>

              <div className="form-group">
                <label>–ö–∞—Ç–µ–≥–æ—Ä—ñ—è</label>
                <select
                  name="category"
                  value={formData.category}
                  onChange={handleInputChange}
                  className="select-input"
                >
                  <option value="general">–ó–∞–≥–∞–ª—å–Ω–∞</option>
                  <option value="classes">–ó–∞–Ω—è—Ç—Ç—è</option>
                  <option value="events">–ü–æ–¥—ñ—ó</option>
                  <option value="concerts">–ö–æ–Ω—Ü–µ—Ä—Ç–∏</option>
                  <option value="exhibitions">–í–∏—Å—Ç–∞–≤–∫–∏</option>
                </select>
              </div>

              <div className="form-actions">
                <button 
                  type="button" 
                  onClick={cancelForm}
                  className="cancel-btn"
                >
                  –°–∫–∞—Å—É–≤–∞—Ç–∏
                </button>
                <button 
                  type="submit" 
                  disabled={
                    (uploadMethod === 'file' && !selectedFile) ||
                    (uploadMethod === 'link' && !formData.image_url.trim()) ||
                    !formData.title.trim() || 
                    uploading
                  }
                  className="submit-btn"
                >
                  {uploading ? '–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...' : '–î–æ–¥–∞—Ç–∏ —Ñ–æ—Ç–æ'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* –°–ø–∏—Å–æ–∫ —Ñ–æ—Ç–æ */}
      <div className="images-list">
        <h2>–§–æ—Ç–æ –≤ –≥–∞–ª–µ—Ä–µ—ó ({images.length})</h2>

        {loading ? (
          <div className="loading">–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...</div>
        ) : (
          <div className="images-grid">
            {images.map((image) => (
              <div key={image.id} className="image-card">
                <div className="image-container">
                  <img 
                    src={image.image_url} 
                    alt={image.title}
                    onError={(e) => {
                      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è –∑–æ–±—Ä–∞–∂–µ–Ω–Ω—è:', image.image_url);
                      e.target.src = '/img/placeholder.jpg';
                    }}
                  />
                  <div className="image-overlay">
                    <button 
                      onClick={() => handleDelete(image.id)}
                      className="delete-btn"
                    >
                      üóëÔ∏è
                    </button>
                  </div>
                </div>
                <div className="image-info">
                  <h4>{image.title}</h4>
                  {image.description && <p>{image.description}</p>}
                  <div className="image-meta">
                    <span className="category">{image.category}</span>
                    <span className="date">
                      {image.created_at ? new Date(image.created_at).toLocaleDateString('uk-UA') : '–ù–µ –≤–∫–∞–∑–∞–Ω–æ'}
                    </span>
                  </div>
                  {image.image_url && image.image_url.includes('google.com') && (
                    <div className="source-badge">üåê Google Drive</div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}

        {images.length === 0 && !loading && (
          <div className="empty-state">
            <div className="empty-icon">üñºÔ∏è</div>
            <h3>–©–µ –Ω–µ–º–∞—î –¥–æ–¥–∞–Ω–∏—Ö —Ñ–æ—Ç–æ</h3>
            <p>–î–æ–¥–∞–π—Ç–µ –ø–µ—Ä—à–µ —Ñ–æ—Ç–æ –¥–æ –≥–∞–ª–µ—Ä–µ—ó</p>
            <button 
              onClick={() => setShowForm(true)}
              className="add-first-btn"
            >
              –î–æ–¥–∞—Ç–∏ –ø–µ—Ä—à–µ —Ñ–æ—Ç–æ
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default GalleryManagement;
