import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { contentService } from '../../services/contentService';
import './SitePage.css';

const SitePage = () => {
  const { pageName } = useParams();
  const [pageData, setPageData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadPageData();
  }, [pageName]);

  const loadPageData = async () => {
    try {
      const response = await contentService.getContent(pageName);
      if (response.data && response.data.content) {
        // Якщо дані є в базі
        setPageData({
          title: getPageTitle(pageName),
          content: response.data.content
        });
      } else {
        // Якщо даних немає, використовуємо шаблон
        setPageData(getDefaultTemplate(pageName));
      }
    } catch (error) {
      console.error('Помилка завантаження:', error);
      setPageData(getDefaultTemplate(pageName));
    } finally {
      setLoading(false);
    }
  };

  const getPageTitle = (name) => {
    const titles = {
      home: 'УМАНСЬКИЙ ЛІЦЕЙ № 2',
      about: 'Про ліцей',
      contact: 'Контакти',
      news: 'Новини',
      teachers: 'Вчителі',
      schedule: 'Розклад'
    };
    return titles[name] || name;
  };

  const getDefaultTemplate = (pageName) => {
    const templates = {
      home: {
        title: 'УМАНСЬКИЙ ЛІЦЕЙ № 2',
        content: `
          <div class="hero-section">
            <h1>Уманський ліцей № 2</h1>
            <h2>Уманської міської ради Черкаської області</h2>
            <p>ЗАПРОШУЄ НА НАВЧАННЯ ЗДОБУВАЧІВ ОСВІТИ ДО 5-11-Х КЛАСІВ.</p>
            <a href="https://docs.google.com/forms/d/1qGynAqMyNhabwivzZtY9kXX9_ChNE49KsfcKbKfzMwo/viewform" class="cta-button">Подати заявку</a>
          </div>
          <div class="content-section">
            <h3>Прозорість та інформаційна відкритість діяльності закладу</h3>
            <p>Тут буде інформація про прозорість діяльності...</p>

            <h3>Виховна робота</h3>
            <p>Інформація про виховну роботу...</p>

            <h3>Оголошення</h3>
            <p>Актуальні оголошення...</p>

            <h3>Відсотока</h3>
            <p>Інформація про відсотоку...</p>
          </div>
        `
      },
      about: {
        title: 'Про ліцей',
        content: `
          <h1>Про наш ліцей</h1>
          <p>Уманський ліцей № 2 - це сучасний навчальний заклад з багаторічною історією...</p>
        `
      },
      contact: {
        title: 'Контакти',
        content: `
          <h1>Контактна інформація</h1>
          <p>Адреса: м. Умань, вул. Шкільна, 1</p>
          <p>Телефон: +380 (44) 123-4567</p>
          <p>Email: school@example.com</p>
        `
      }
    };

    return templates[pageName] || {
      title: getPageTitle(pageName),
      content: `<h1>Сторінка "${pageName}"</h1><p>Ця сторінка ще не створена.</p>`
    };
  };

  if (loading) {
    return (
      <div className="site-loading">
        <div>Завантаження сторінки...</div>
      </div>
    );
  }

  if (!pageData) {
    return (
      <div className="site-error">
        <h1>Сторінку не знайдено</h1>
        <p>Сторінка "{pageName}" не існує.</p>
        <Link to="/site/home">Перейти на головну</Link>
      </div>
    );
  }

  return (
    <div className="site-page">
      {/* Хедер сайту */}
      <header className="site-header">
        <div className="header-content">
          <div className="site-title">
            <h1>УМАНСЬКИЙ ЛІЦЕЙ № 2</h1>
            <p>Уманської міської ради Черкаської області</p>
          </div>

          <nav className="main-menu">
            <ul>
              <li><Link to="/site/home">Головна</Link></li>
              <li><Link to="/site/about">Мій ліцей</Link></li>
              <li><Link to="/site/contact">Нормативно-правова база</Link></li>
              <li><Link to="/site/news">Діяльність ліцею</Link></li>
              <li><Link to="/site/teachers">Форми навчання</Link></li>
              <li><Link to="/admin/dashboard" style={{ color: '#e74c3c' }}>Адмін-панель</Link></li>
            </ul>
          </nav>
        </div>
      </header>

      {/* Основний контент */}
      <main className="site-content">
        <div 
          className="page-content"
          dangerouslySetInnerHTML={{ __html: pageData.content }}
        />
      </main>

      {/* Футер */}
      <footer className="site-footer">
        <div className="footer-content">
          <p>&copy; 2024 Уманський ліцей № 2. Всі права захищені.</p>
          <p>
            <a href="/admin/dashboard" style={{ color: '#3498db', fontSize: '0.9rem' }}>
              Вхід для адміністратора
            </a>
          </p>
        </div>
      </footer>
    </div>
  );
};

export default SitePage;
