import React, { useState, useEffect } from 'react';
import { transparencyService } from '../../services/transparencyService';
import RichTextEditor from '../editor/RichTextEditor';
import './TransparencyManagement.css';

const TransparencyManagement = () => {
  const [sections, setSections] = useState([]);
  const [loading, setLoading] = useState(true);
  const [editingSection, setEditingSection] = useState(null);
  const [saveStatus, setSaveStatus] = useState('');
  const [uploading, setUploading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    loadSections();
  }, []);

  const loadSections = async () => {
    try {
      setLoading(true);
      setError('');
      const data = await transparencyService.getAll();
      setSections(data);
    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ä–æ–∑–¥—ñ–ª—ñ–≤:', error);
      setError('–ù–µ –≤–¥–∞–ª–æ—Å—è –∑–∞–≤–∞–Ω—Ç–∞–∂–∏—Ç–∏ —Ä–æ–∑–¥—ñ–ª–∏ –ø—Ä–æ–∑–æ—Ä–æ—Å—Ç—ñ');
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (section) => {
    setEditingSection({ 
      ...section,
      newDocuments: []
    });
  };

  const handleContentChange = (content) => {
    setEditingSection(prev => ({
      ...prev,
      content: content
    }));
  };

  const handleFileUpload = async (event) => {
    const files = Array.from(event.target.files);
    if (!files.length) return;

    setUploading(true);

    try {
      const uploadedDocuments = [];

      for (const file of files) {
        // –¢–∏–º—á–∞—Å–æ–≤–µ —Ä—ñ—à–µ–Ω–Ω—è –¥–ª—è –¥–µ–º–æ–Ω—Å—Ç—Ä–∞—Ü—ñ—ó - –≤ —Ä–µ–∞–ª—å–Ω–æ–º—É –¥–æ–¥–∞—Ç–∫—É —Ç—É—Ç –±—É–¥–µ –∑–∞–ø–∏—Ç –¥–æ API
        uploadedDocuments.push({
          name: file.name,
          url: URL.createObjectURL(file), // –¢–∏–º—á–∞—Å–æ–≤–∏–π URL
          type: file.type,
          size: file.size
        });
      }

      setEditingSection(prev => ({
        ...prev,
        documents: [...(prev.documents || []), ...uploadedDocuments]
      }));

      setSaveStatus(`‚úÖ –£—Å–ø—ñ—à–Ω–æ –¥–æ–¥–∞–Ω–æ ${uploadedDocuments.length} —Ñ–∞–π–ª—ñ–≤`);
      setTimeout(() => setSaveStatus(''), 3000);

    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ñ–∞–π–ª—ñ–≤:', error);
      setSaveStatus('‚ùå –ü–æ–º–∏–ª–∫–∞ –∑–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è —Ñ–∞–π–ª—ñ–≤');
      setTimeout(() => setSaveStatus(''), 3000);
    } finally {
      setUploading(false);
      // –û—á–∏—â–∞—î–º–æ input
      event.target.value = '';
    }
  };

  const removeDocument = (index) => {
    setEditingSection(prev => ({
      ...prev,
      documents: prev.documents.filter((_, i) => i !== index)
    }));
  };

  const handleSave = async () => {
    if (!editingSection) return;

    try {
      setSaveStatus('–ó–±–µ—Ä–µ–∂–µ–Ω–Ω—è...');
      await transparencyService.update(editingSection.section_type, {
        title: editingSection.title,
        content: editingSection.content,
        documents: editingSection.documents || []
      });

      setSaveStatus('‚úÖ –ó–±–µ—Ä–µ–∂–µ–Ω–æ —É—Å–ø—ñ—à–Ω–æ!');
      setTimeout(() => setSaveStatus(''), 3000);

      // –û–Ω–æ–≤–ª—é—î–º–æ —Å–ø–∏—Å–æ–∫
      loadSections();
      setEditingSection(null);
    } catch (error) {
      console.error('–ü–æ–º–∏–ª–∫–∞ –∑–±–µ—Ä–µ–∂–µ–Ω–Ω—è:', error);
      setSaveStatus('‚ùå –ü–æ–º–∏–ª–∫–∞ –∑–±–µ—Ä–µ–∂–µ–Ω–Ω—è');
      setTimeout(() => setSaveStatus(''), 3000);
    }
  };

  const handleCancel = () => {
    setEditingSection(null);
    setSaveStatus('');
  };

  if (loading) return <div className="loading">–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...</div>;

  return (
    <div className="transparency-management">
      <div className="page-header">
        <h1>–£–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è —Ä–æ–∑–¥—ñ–ª–æ–º "–ü—Ä–æ–∑–æ—Ä—ñ—Å—Ç—å"</h1>
        <p>–†–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è –∫–æ–Ω—Ç–µ–Ω—Ç—É –¥–ª—è —Ä–æ–∑–¥—ñ–ª—ñ–≤ –ø—Ä–æ–∑–æ—Ä–æ—Å—Ç—ñ —Ç–∞ —ñ–Ω—Ñ–æ—Ä–º–∞—Ü—ñ–π–Ω–æ—ó –≤—ñ–¥–∫—Ä–∏—Ç–æ—Å—Ç—ñ</p>
      </div>

      {error && (
        <div className="alert alert-danger">
          {error}
          <button onClick={loadSections} style={{marginLeft: '10px'}}>
            –°–ø—Ä–æ–±—É–≤–∞—Ç–∏ –∑–Ω–æ–≤—É
          </button>
        </div>
      )}

      {saveStatus && (
        <div className={`alert ${saveStatus.includes('‚úÖ') ? 'alert-success' : 'alert-danger'}`}>
          {saveStatus}
        </div>
      )}

      {editingSection ? (
        <div className="editor-section">
          <div className="editor-header">
            <h2>–†–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è: {editingSection.title}</h2>
            <div className="editor-actions">
              <button className="btn btn-secondary" onClick={handleCancel}>
                –°–∫–∞—Å—É–≤–∞—Ç–∏
              </button>
              <button className="btn btn-primary" onClick={handleSave}>
                –ó–±–µ—Ä–µ–≥—Ç–∏ –∑–º—ñ–Ω–∏
              </button>
            </div>
          </div>

          <div className="content-editor">
            <label>–ö–æ–Ω—Ç–µ–Ω—Ç:</label>
            <RichTextEditor
              value={editingSection.content || ''}
              onChange={handleContentChange}
              height="400px"
            />
          </div>

          <div className="documents-section">
            <label>–î–æ–∫—É–º–µ–Ω—Ç–∏:</label>
            <div className="documents-upload">
              <input
                type="file"
                multiple
                onChange={handleFileUpload}
                disabled={uploading}
                className="file-input"
                accept=".pdf,.doc,.docx,.jpg,.jpeg,.png"
              />
              {uploading && <div className="uploading">–ó–∞–≤–∞–Ω—Ç–∞–∂–µ–Ω–Ω—è...</div>}
            </div>

            <div className="documents-list">
              {editingSection.documents?.map((doc, index) => (
                <div key={index} className="document-item">
                  <span className="document-name">{doc.name}</span>
                  <div className="document-actions">
                    <a 
                      href={doc.url} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="btn btn-sm btn-outline-primary"
                    >
                      –ü–µ—Ä–µ–≥–ª—è–Ω—É—Ç–∏
                    </a>
                    <button
                      className="btn btn-sm btn-outline-danger"
                      onClick={() => removeDocument(index)}
                    >
                      –í–∏–¥–∞–ª–∏—Ç–∏
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      ) : (
        <div className="sections-grid">
          {sections.length === 0 ? (
            <div className="no-sections">
              <p>–†–æ–∑–¥—ñ–ª–∏ –ø—Ä–æ–∑–æ—Ä–æ—Å—Ç—ñ –Ω–µ –∑–Ω–∞–π–¥–µ–Ω—ñ –∞–±–æ —â–µ –Ω–µ —Å—Ç–≤–æ—Ä–µ–Ω—ñ.</p>
              <button onClick={loadSections} className="btn btn-primary">
                –û–Ω–æ–≤–∏—Ç–∏
              </button>
            </div>
          ) : (
            sections.map(section => (
              <div key={section.id} className="transparency-card">
                <div className="card-header">
                  <h3>{section.title}</h3>
                  <div className="card-status">
                    <span className={`status ${section.content ? 'active' : 'empty'}`}>
                      {section.content ? '–ù–∞–ø–æ–≤–Ω–µ–Ω–∏–π' : '–ü–æ—Ä–æ–∂–Ω—ñ–π'}
                    </span>
                    {section.documents && section.documents.length > 0 && (
                      <span className="documents-count">
                        üìé {section.documents.length}
                      </span>
                    )}
                  </div>
                </div>

                <div className="card-content">
                  {section.content ? (
                    <div 
                      className="content-preview"
                      dangerouslySetInnerHTML={{ __html: section.content }}
                    />
                  ) : (
                    <p className="empty-state">–ö–æ–Ω—Ç–µ–Ω—Ç —â–µ –Ω–µ –¥–æ–¥–∞–Ω–æ</p>
                  )}
                </div>

                {section.documents && section.documents.length > 0 && (
                  <div className="card-documents">
                    <strong>–î–æ–∫—É–º–µ–Ω—Ç–∏:</strong>
                    <div className="documents-preview">
                      {section.documents.slice(0, 3).map((doc, index) => (
                        <span key={index} className="doc-preview">üìÑ {doc.name}</span>
                      ))}
                      {section.documents.length > 3 && (
                        <span className="doc-more">+{section.documents.length - 3} —â–µ</span>
                      )}
                    </div>
                  </div>
                )}

                <div className="card-actions">
                  <button 
                    className="btn btn-primary"
                    onClick={() => handleEdit(section)}
                  >
                    –†–µ–¥–∞–≥—É–≤–∞—Ç–∏
                  </button>
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
};

export default TransparencyManagement;
