import React from 'react';
import { useParams } from 'react-router-dom';
import SiteLayout from '../layout/SiteLayout';
import './SitePage.css';

// Імпортуємо всі статичні компоненти
import Home from '../static/Home';
import About from '../static/About';
import Departments from '../static/Departments';
import Contacts from '../static/Contacts';
import Gallery from '../static/Gallery';
import Events from '../static/Events';
import Transparency from '../static/Transparency';
import NewsList from '../news/NewsList';
import NewsItem from '../news/NewsItem';

const SitePage = () => {
  const { pageName } = useParams();

  // Функція для рендерингу контенту на основі pageName
  const renderContent = () => {
    switch (pageName) {
      case 'home':
        return <Home />;
      case 'about':
        return <About />;
      case 'departments':
        return <Departments />;
      case 'contacts':
        return <Contacts />;
      case 'gallery':
        return <Gallery />;
      case 'events':
        return <Events />;
      case 'transparency':
        return <Transparency />;
      case 'news':
        return <NewsList />;
      default:
        // Для невідомих сторінок
        return (
          <div style={{ padding: '2rem', textAlign: 'center' }}>
            <h1>Сторінку не знайдено</h1>
            <p>Сторінка "{pageName}" не існує.</p>
            <a href="/site/home">Повернутися на головну</a>
          </div>
        );
    }
  };

  return (
    <SiteLayout>
      {renderContent()}
    </SiteLayout>
  );
};

// Окремий компонент для сторінки новини (якщо потрібен)
export const NewsPage = () => {
  const { id } = useParams();

  if (id) {
    return (
      <SiteLayout>
        <NewsItem />
      </SiteLayout>
    );
  }

  return (
    <SiteLayout>
      <NewsList />
    </SiteLayout>
  );
};

export default SitePage;
