import React from 'react';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  // –¢—ñ–ª—å–∫–∏ —Å—Ç–æ—Ä—ñ–Ω–∫–∏, —è–∫—ñ –º–æ–∂–Ω–∞ —Ä–µ–¥–∞–≥—É–≤–∞—Ç–∏
  const editablePages = [
    { name: '–ù–æ–≤–∏–Ω–∏', path: 'news', description: '–ö–µ—Ä—É–≤–∞–Ω–Ω—è –Ω–æ–≤–∏–Ω–∞–º–∏ —à–∫–æ–ª–∏', icon: 'üì∞', color: '#9b59b6' },
    { name: '–§–æ—Ç–æ/–í—ñ–¥–µ–æ', path: 'gallery', description: '–†–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è –≥–∞–ª–µ—Ä–µ—ó —Ñ–æ—Ç–æ —Ç–∞ –≤—ñ–¥–µ–æ', icon: 'üñºÔ∏è', color: '#e74c3c' },
    { name: '–ü—Ä–æ–∑–æ—Ä—ñ—Å—Ç—å', path: 'transparency', description: '–î–æ–∫—É–º–µ–Ω—Ç–∏ —Ç–∞ –∑–≤—ñ—Ç–Ω—ñ—Å—Ç—å —à–∫–æ–ª–∏', icon: 'üìä', color: '#3498db' }
  ];

  return (
    <div style={{ padding: '2rem', maxWidth: '1200px', margin: '0 auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h1 style={{ fontSize: '2.5rem', color: '#2c3e50', margin: 0 }}>
          –ü–∞–Ω–µ–ª—å —É–ø—Ä–∞–≤–ª—ñ–Ω–Ω—è —Å–∞–π—Ç–æ–º
        </h1>
        <a 
          href="/site/home" 
          target="_blank"
          rel="noopener noreferrer"
          style={{
            backgroundColor: '#27ae60',
            color: 'white',
            padding: '0.75rem 1.5rem',
            textDecoration: 'none',
            borderRadius: '5px',
            fontWeight: 'bold'
          }}
        >
          üëÅÔ∏è –ü–µ—Ä–µ–≥–ª—è–¥ —Å–∞–π—Ç—É
        </a>
      </div>

      {/* –°–µ–∫—Ü—ñ—è —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è —Å—Ç–æ—Ä—ñ–Ω–æ–∫ */}
      <div>
        <h2 style={{ color: '#2c3e50', marginBottom: '1.5rem', borderBottom: '2px solid #3498db', paddingBottom: '0.5rem' }}>
          –†–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è —Å—Ç–æ—Ä—ñ–Ω–æ–∫
        </h2>
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(350px, 1fr))',
          gap: '1.5rem'
        }}>
          {editablePages.map((page) => (
            <div key={page.path} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
              <Link
                to={`/admin/visual-editor/${page.path}`}
                style={{
                  background: 'white',
                  padding: '2rem',
                  borderRadius: '10px',
                  boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
                  textDecoration: 'none',
                  color: 'inherit',
                  border: `2px solid ${page.color}`,
                  transition: 'all 0.3s ease',
                  display: 'block'
                }}
                onMouseOver={(e) => {
                  e.currentTarget.style.borderColor = page.color;
                  e.currentTarget.style.transform = 'translateY(-5px)';
                  e.currentTarget.style.boxShadow = '0 8px 25px rgba(0,0,0,0.15)';
                }}
                onMouseOut={(e) => {
                  e.currentTarget.style.borderColor = page.color;
                  e.currentTarget.style.transform = 'translateY(0)';
                  e.currentTarget.style.boxShadow = '0 4px 6px rgba(0,0,0,0.1)';
                }}
              >
                <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
                  <span style={{ fontSize: '2rem' }}>{page.icon}</span>
                  <h3 style={{ 
                    fontSize: '1.5rem', 
                    margin: 0,
                    color: '#2c3e50'
                  }}>
                    {page.name}
                  </h3>
                </div>
                <p style={{ 
                  color: '#7f8c8d',
                  margin: 0,
                  lineHeight: '1.5'
                }}>
                  {page.description}
                </p>
                <div style={{
                  marginTop: '1rem',
                  padding: '0.5rem',
                  background: '#ecf0f1',
                  borderRadius: '5px',
                  fontSize: '0.9rem',
                  color: '#34495e',
                  textAlign: 'center'
                }}>
                  üìù –í—ñ–∑—É–∞–ª—å–Ω–µ —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è
                </div>
              </Link>

              <div style={{ display: 'flex', gap: '5px' }}>
                <Link
                  to={`/admin/editor/${page.path}`}
                  style={{
                    flex: 1,
                    textAlign: 'center',
                    padding: '0.5rem',
                    background: '#95a5a6',
                    color: 'white',
                    textDecoration: 'none',
                    borderRadius: '5px',
                    fontSize: '0.8rem'
                  }}
                >
                  –¢–µ–∫—Å—Ç–æ–≤–∏–π —Ä–µ–¥–∞–∫—Ç–æ—Ä
                </Link>

                <a 
                  href={`/site/${page.path}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  style={{
                    flex: 1,
                    textAlign: 'center',
                    padding: '0.5rem',
                    background: '#27ae60',
                    color: 'white',
                    textDecoration: 'none',
                    borderRadius: '5px',
                    fontSize: '0.8rem'
                  }}
                >
                  –ü–µ—Ä–µ–≥–ª—è–¥
                </a>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* –°–µ–∫—Ü—ñ—è –∫–µ—Ä—É–≤–∞–Ω–Ω—è –Ω–æ–≤–∏–Ω–∞–º–∏ */}
      <div style={{ marginTop: '3rem' }}>
        <h2 style={{ color: '#2c3e50', marginBottom: '1.5rem', borderBottom: '2px solid #9b59b6', paddingBottom: '0.5rem' }}>
          –ö–µ—Ä—É–≤–∞–Ω–Ω—è –∫–æ–Ω—Ç–µ–Ω—Ç–æ–º
        </h2>
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '1.5rem'
        }}>
          <Link
            to="/admin/news"
            style={{
              background: 'white',
              padding: '2rem',
              borderRadius: '10px',
              boxShadow: '0 4px 6px rgba(0,0,0,0.1)',
              textDecoration: 'none',
              color: 'inherit',
              border: '2px solid #9b59b6',
              transition: 'all 0.3s ease',
              display: 'block'
            }}
            onMouseOver={(e) => {
              e.currentTarget.style.borderColor = '#9b59b6';
              e.currentTarget.style.transform = 'translateY(-5px)';
              e.currentTarget.style.boxShadow = '0 8px 25px rgba(155, 89, 182, 0.2)';
            }}
            onMouseOut={(e) => {
              e.currentTarget.style.borderColor = '#9b59b6';
              e.currentTarget.style.transform = 'translateY(0)';
              e.currentTarget.style.boxShadow = '0 4px 6px rgba(0,0,0,0.1)';
            }}
          >
            <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', marginBottom: '1rem' }}>
              <span style={{ fontSize: '2rem' }}>üì∞</span>
              <h3 style={{ 
                fontSize: '1.5rem', 
                margin: 0,
                color: '#2c3e50'
              }}>
                –ö–µ—Ä—É–≤–∞–Ω–Ω—è –Ω–æ–≤–∏–Ω–∞–º–∏
              </h3>
            </div>
            <p style={{ 
              color: '#7f8c8d',
              margin: 0,
              lineHeight: '1.5'
            }}>
              –°—Ç–≤–æ—Ä–µ–Ω–Ω—è, —Ä–µ–¥–∞–≥—É–≤–∞–Ω–Ω—è —Ç–∞ –ø—É–±–ª—ñ–∫–∞—Ü—ñ—è –Ω–æ–≤–∏–Ω —à–∫–æ–ª–∏
            </p>
            <div style={{
              marginTop: '1rem',
              padding: '0.5rem',
              background: '#f4ecf7',
              borderRadius: '5px',
              fontSize: '0.9rem',
              color: '#8e44ad',
