import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000/api', // –∞–±–æ 'http://192.168.0.224:5000/api'
  timeout: 10000,
});

// –î–æ–¥–∞–π—Ç–µ –ø–µ—Ä–µ—Ö–æ–ø–ª—é–≤–∞—á –¥–ª—è –≤—ñ–¥–ª–∞–¥–∫–∏
api.interceptors.request.use(request => {
  //console.log('üîÑ Axios Request:', request.method?.toUpperCase(), request.url);
  //console.log('üì¶ Request Data:', request.data);
  return request;
});

api.interceptors.response.use(
  response => {
    //console.log('‚úÖ Axios Response:', response.status, response.data);
    return response;
  },
  error => {
    //console.log('‚ùå Axios Error:', error.response?.status, error.message);
    return Promise.reject(error);
  }
);

export default api;
